import React, { useState, useEffect ,useCallback} from 'react';
import axios from 'axios';

const FetchUser = () => {
    const [username, setUsername] = useState('');
    const [user, setUser] = useState(null);
    const [repositories, setRepositories] = useState([]);
  
    const handleChange = (e) => {
      setUsername(e.target.value);
    };
  
    const handleSubmit = useCallback(async (e) => {
      e.preventDefault();
  
      try {
        // Fetch user data
        const userResponse = await axios.get(`https://api.github.com/users/${username}`);
        setUser(userResponse.data);
  
        // Fetch user repositories
        const repoResponse = await axios.get(`https://api.github.com/users/${username}/repos`);
        setRepositories(repoResponse.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    }, [username]);
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          // Set a default username
          const defaultUsername = 'yourDefaultUsername'; // Replace with your desired default username
          setUsername(defaultUsername);
  
          // Call the handleSubmit function
          await handleSubmit({ preventDefault: () => {} });
        } catch (error) {
          console.error('Error during default data fetch:', error);
        }
      };
  
      fetchData();
    }, [handleSubmit]);

  return (
    <>
      <div className="container-fluid mt-5">
        <div className="row text-center">
          <div className="col-12">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="githubUsername" className="form-label">
                  GitHub Username:
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="githubUsername"
                  value={username}
                  onChange={handleChange}
                />
              </div>
              <button type="submit" className="btn btn-primary">
                Search
              </button>
            </form>
          </div>
          {user && (
            <div className="col-12 mt-3">
              <div className="card p-3">
                <img
                  src={user.avatar_url}
                  alt=""
                  className="rounded-circle"
                  width="100"
                  height="100"
                />
                <h3 className="mt-3">{user.login}</h3>
                <p>Followers: {user.followers}</p>
                <p>Following: {user.following}</p>
                <p>Public Repositories: {user.public_repos}</p>
              </div>
            </div>
          )}
          <div className="col-12 mt-3">
            <h3>Repositories</h3>
            <ul className="list-group">
              {repositories.map((repo) => (
                <li key={repo.id} className="list-group-item">
                  <a href={repo.html_url} target="_blank" rel="noopener noreferrer">
                    {repo.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </>
  );
};

export default FetchUser;
