// RepositoryDetails.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RepositoryDetails = ({ match }) => {
  const [repository, setRepository] = useState(null);

  const getRepositoryDetails = async () => {
    try {
      const response = await axios.get(`https://api.github.com/repos/${match.params.username}/${match.params.repoName}`);
      setRepository(response.data);
    } catch (error) {
      console.error('Error fetching repository details:', error);
    }
  };

  useEffect(() => {
    getRepositoryDetails();
  }, [match.params.username, match.params.repoName]);

  if (!repository) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-8">
          <h2>{repository.name}</h2>
          <p>{repository.description}</p>
          <p>Language: {repository.language}</p>
          <p>Stars: {repository.stargazers_count}</p>
          <p>Forks: {repository.forks_count}</p>
          {/* Add more details as needed */}
        </div>
      </div>
    </div>
  );
};

export default RepositoryDetails;
